    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
      Branch branch = new Branch();
      if (!"".equals(branchName.getText()) && !"".equals(branchAdress.getText()) && !"".equals(branchRooms.getText())) {
          branch.setNombre(branchName.getText());
          branch.setAddress(branchAdress.getText());
          branch.setID(Integer.parseInt(branchRooms.getText()));
          branch.setRooms(Integer.parseInt(branchRooms.getText()));
          for(int i=0; i<EmpresaCine.sucursal.length; i++){
              branch.setID(i);
               if (EmpresaCine.sucursal[i] == null) {
                   EmpresaCine.sucursal[i] = branch;
                   JOptionPane.showMessageDialog(null, "Sucursal Guardada Correctamente");
                    break;
               }
          } for (int z = 0; z < EmpresaCine.sucursal.length; z++) {
                if (EmpresaCine.sucursal[z] != null) {
                    setArea.append(EmpresaCine.sucursal[z].toString() + "\n");
                }
      }
      }else{
          JOptionPane.showMessageDialog(rootPane, "Ninguno de los campos puede estar vacio");
      }
    }//GEN-LAST:event_jButton3ActionPerformed

