    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
      if (!"".equals(place.getText()) && !"".equals(nombre.getText()) && !"".equals(direccion.getText()) && !"".equals(salas.getText())) {
          int posicion = Integer.parseInt(place.getText());
          Branch sucu = new Branch();
          sucu.setID(posicion);
          sucu.setNombre(nombre.getText());
          sucu.setAddress(direccion.getText());
          sucu.setRooms(Integer.parseInt(salas.getText()));
          if (EmpresaCine.sucursal[posicion] != null) {
              EmpresaCine.sucursal[posicion] = sucu;
                JOptionPane.showMessageDialog(rootPane, "Sucursal modificada correctamente");
          }else {
                System.out.println("No existe ninguna sucursal con ese identificador");
                JOptionPane.showMessageDialog(rootPane, "No existe ninguna sucursal con ese identificador");
      }}else {
            JOptionPane.showMessageDialog(rootPane, "El número de Sucursal no puede estar vacio");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       String lugar = eliminarTexto.getText();
        if (!"".equals(lugar)) {
            if (EmpresaCine.sucursal[Integer.parseInt(lugar)] != null) {
                EmpresaCine.sucursal[Integer.parseInt(lugar)] = null;
                JOptionPane.showMessageDialog(rootPane, "Sucursal eliminada correctamente");
            } else {
                System.out.println("El número de Sucursal que desea eliminar no existe");
                JOptionPane.showMessageDialog(rootPane, "No existe ninguna Sucursal con ese identificador");
            }
        } else {
            JOptionPane.showMessageDialog(rootPane, "El número de Sucursal no puede estar vacio");
        }        // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

